TriggerOraclev10g.body:
BEGIN
  IF (UPDATING) THEN
        IF :new.SISTEMA_TIPO_AGENTE IS NOT NULL AND :old.SISTEMA_TIPO_AGENTE IS NOT NULL THEN
            :new.SISTEMA_TIPO_AGENTE := :old.SISTEMA_TIPO_AGENTE;
        END IF;
        :new.AUD_DATE_UPDATE               := TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS');
  END IF;

  if INSERTING then
       :new.AUD_DATE_INSERT               := TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'); 
  END IF;
EXCEPTION
     WHEN OTHERS THEN
       -- 
       RAISE ;
END ;
