DECLARE
   wNumerador  NUMBER;
   v_accion VARCHAR2(1);
BEGIN
    SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

    INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, VALOR_ANT)
    VALUES(wNumerador, 'FIX', 'WS_COBERTURA_FIX_PARAM', :OLD.ID_COBERTURA_FIX_PARAM,'P','B',:OLD.SCRIPT);
END;
