TriggerOraclev10g.body:
DECLARE
   ERROR_GRABA EXCEPTION;
   ZOPERACION   CHAR (1);
BEGIN
   IF UPDATING
   THEN
      ZOPERACION := 'M';
   ELSE
      IF DELETING
      THEN
         ZOPERACION := 'B';
      ELSE
         ZOPERACION := '?';
      END IF;
   END IF;
   INSERT INTO RAPIPAGO.WS_PUESTO_PROPERTIES_LOG (ID_PUESTO_PROPERTY, PUESTO_PROPERTY, PUESTO_PROPERTY_VALUE, IS_DELETED, AUD_USER_INSERT, AUD_DATE_INSERT, AUD_USER_UPDATE, AUD_DATE_UPDATE, OPERACION, MODIFICADO_POR, FH_MODIFICACION)
     VALUES   (:VIEJO.ID_PUESTO_PROPERTY, :VIEJO.PUESTO_PROPERTY, :VIEJO.PUESTO_PROPERTY_VALUE, :VIEJO.IS_DELETED, :VIEJO.AUD_USER_INSERT, :VIEJO.AUD_DATE_INSERT, :VIEJO.AUD_USER_UPDATE, :VIEJO.AUD_DATE_UPDATE,
               ZOPERACION,
               Usuario_Update (:NUEVO.AUD_USER_UPDATE),
               TO_CHAR (SYSDATE, 'YYYY-MM-DD HH24:MI:SS'));
EXCEPTION
   WHEN OTHERS
   THEN
      RAISE ERROR_GRABA;
END;
