TriggerOraclev10g.body:
DECLARE
BEGIN
  IF (UPDATING) THEN

        :new.AUD_DATE_UPDATE := TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') ;
        :new.AUD_USER_UPDATE := UPPER(substr(user, 1, 30)) ;
  END IF ;

  if INSERTING then
       :new.AUD_DATE_INSERT  := TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') ; 
       :new.AUD_USER_INSERT  := UPPER(substr(user, 1, 30)) ;
  END IF ;
EXCEPTION
     WHEN OTHERS THEN
       -- 
       RAISE ;
END ;
