TriggerOraclev10g.body:
BEGIN
IF (UPDATING) THEN
    :new.AUD_DATE_UPDATE := TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS');
    IF :new.AUD_USER_UPDATE IS NULL OR :new.AUD_USER_UPDATE = :old.AUD_USER_UPDATE THEN
       :new.AUD_USER_UPDATE := SUBSTR (USER, 1, 15);
    END IF;
END IF;

IF (INSERTING) then
    :new.AUD_DATE_INSERT := TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS');
    IF :new.AUD_USER_INSERT IS NULL THEN
       :new.AUD_USER_INSERT := SUBSTR (USER, 1, 15);
    END IF;
    IF :new.LIMITE_ACTIVIDAD_DIARIA IS NULL THEN
        :new.LIMITE_ACTIVIDAD_DIARIA := '23:30';
    END IF;
END IF;


EXCEPTION
     WHEN OTHERS THEN
       -- 
       RAISE ;
END ;
