TriggerOraclev10g.body:
DECLARE
   wNumerador  NUMBER;
   v_accion VARCHAR2(1);
BEGIN
    SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;

    IF :OLD.OPERACION IN ('EX', 'ET') THEN
       v_accion := 'I';
    ELSE 
       v_accion := 'B';
    END IF;

    INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION)
    VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO_COBERTURA', :OLD.ID_EMPRESA_PUESTO_COBERTURA,'P',v_accion);

END;
