TriggerOraclev10g.body:
begin
   if :new.ID_PRESTATARIA_CONECTIVIDAD is not null then 
      if inserting or (updating and nvl(:OLd.ID_PRESTATARIA_CONECTIVIDAD, 'nulo') <> :new.ID_PRESTATARIA_CONECTIVIDAD) then        
         select id_tipo_conectividad into :new.id_tipo_conectividad  
           from RAPIPAGO.RP_PRESTATARIA_CONECTIVIDAD
          where id_prestataria_conectividad = :NEW.id_prestataria_conectividad;
      end if;
   end if;
EXCEPTION
     WHEN OTHERS THEN
       -- 
       RAISE ;
END ;
