TriggerOraclev10g.body:
DECLARE
   wNumerador  NUMBER;
   v_accion VARCHAR2(1) := NULL;   
BEGIN

    IF NVL(:OLD.IS_DELETED,0) != NVL(:NEW.IS_DELETED,0) THEN
       IF :NEW.IS_DELETED = 'Y' AND :OLD.WF_STATUS_PUESTO = 'HAB' THEN
          v_accion := 'B';
       END IF;

       IF :NEW.IS_DELETED = 'N' AND :OLD.WF_STATUS_PUESTO = 'HAB' THEN       
          v_accion := 'A';
       END IF;

       IF v_accion IS NOT NULL THEN        
           SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

           INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
              (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
           VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO', :OLD.ID_EMPRESA_PUESTO,'P',v_accion, 'IS_DELETED', :OLD.IS_DELETED, :NEW.IS_DELETED);
       END IF;
    END IF;


    IF NVL(:OLD.WF_STATUS_PUESTO,0) != NVL(:NEW.WF_STATUS_PUESTO,0) THEN
       IF :NEW.WF_STATUS_PUESTO != 'HAB' AND :OLD.WF_STATUS_PUESTO = 'HAB' AND :OLD.IS_DELETED = 'N' THEN
          v_accion := 'B';
       END IF;

       IF :NEW.WF_STATUS_PUESTO = 'HAB' AND :OLD.IS_DELETED = 'N' THEN
          v_accion := 'A';
       END IF;

       IF v_accion IS NOT NULL THEN
            SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

            INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
                (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
            VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO', :OLD.ID_EMPRESA_PUESTO,'P',v_accion, 'WF_STATUS_PUESTO', :OLD.WF_STATUS_PUESTO, :NEW.WF_STATUS_PUESTO);
       END IF;
    END IF;

    IF NVL(:OLD.TOPE_COBRO_MINIMO,0) != NVL(:NEW.TOPE_COBRO_MINIMO,0) THEN

        SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;

        v_accion := 'M';

        INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
        VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO', :OLD.ID_EMPRESA_PUESTO,'P',v_accion, 'TOPE_COBRO_MINIMO', :OLD.TOPE_COBRO_MINIMO, :NEW.TOPE_COBRO_MINIMO);
    END IF;

    IF NVL(:OLD.TOPE_COBRO_MAXIMO,0) != NVL(:NEW.TOPE_COBRO_MAXIMO,0) THEN

        SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;

        v_accion := 'M';

        INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
        VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO', :OLD.ID_EMPRESA_PUESTO,'P',v_accion, 'TOPE_COBRO_MAXIMO', :OLD.TOPE_COBRO_MAXIMO, :NEW.TOPE_COBRO_MAXIMO);
    END IF;

    IF NVL(:OLD.TOPE_MIN_NEG,0) != NVL(:NEW.TOPE_MIN_NEG,0) THEN

        SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;

        v_accion := 'M';

        INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
        VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO', :OLD.ID_EMPRESA_PUESTO,'P',v_accion, 'TOPE_MIN_NEG', :OLD.TOPE_MIN_NEG, :NEW.TOPE_MIN_NEG);
    END IF;

    IF NVL(:OLD.TOPE_MAX_NEG,0) != NVL(:NEW.TOPE_MAX_NEG,0) THEN

        SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;

        v_accion := 'M';

        INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
        VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO', :OLD.ID_EMPRESA_PUESTO,'P',v_accion, 'TOPE_MAX_NEG', :OLD.TOPE_MAX_NEG, :NEW.TOPE_MAX_NEG);
    END IF;

END;
