TriggerOraclev10g.body:
begin
      if nvl(:OLd.WF_STATUS, 'WF_STATUS') <> 'ELI' AND :NEW.WF_STATUS = 'ELI' then
          update rapipago.rp_sucursal_afip
             set IS_DELETED  = 'Y'
           where COD_SUCURSAL = :new.COD_SUCURSAL;
      end if;
   exception
      when no_data_found then
         null;
      when others then
         raise;
   END ;
