TriggerOraclev10g.body:
DECLARE
   wNumerador  NUMBER;
   v_accion VARCHAR2(1);
BEGIN
    IF :NEW.IS_DELETED = 'N' THEN
        SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;

          IF :NEW.OPERACION IN ('EX', 'ET') THEN
            v_accion := 'E';
          ELSE 
            v_accion := 'A';
          END IF;

        INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
            (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION)
        VALUES(wNumerador, 'EMPRESA', 'RP_EMPRESA_PUESTO_COBERTURA', :NEW.ID_EMPRESA_PUESTO_COBERTURA,'P',v_accion);
    END IF;
END;
