TriggerOraclev10g.body:
DECLARE
   wNumerador  NUMBER;
   v_accion VARCHAR2(1) := NULL;
BEGIN
    IF NVL(:OLD.IS_DELETED,0) != NVL(:NEW.IS_DELETED,0) THEN
       IF :NEW.IS_DELETED = 'Y' AND :OLD.ESTADO = 'H' THEN
          v_accion := 'B';
       END IF;

       IF :NEW.IS_DELETED = 'N' AND :OLD.ESTADO = 'H' THEN       
          v_accion := 'A';
       END IF;

       IF v_accion IS NOT NULL THEN      
          SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

          INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
             (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
          VALUES(wNumerador, 'EMPRESA', 'RP_CODIGO_BARRA', :OLD.ID_CODIGO_BARRA,'P',v_accion, 'IS_DELETED', :OLD.IS_DELETED, :NEW.IS_DELETED);
       END IF;
    END IF;


    IF NVL(:OLD.ESTADO,0) != NVL(:NEW.ESTADO,0) THEN
       IF :NEW.ESTADO != 'H' AND :OLD.ESTADO = 'H' AND :OLD.IS_DELETED = 'N' THEN
          v_accion := 'B';
       END IF;

       IF :NEW.ESTADO = 'H' AND :OLD.IS_DELETED = 'N' THEN
          v_accion := 'A';
       END IF;

       IF v_accion IS NOT NULL THEN
            SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

            INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
                (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
            VALUES(wNumerador, 'EMPRESA', 'RP_CODIGO_BARRA', :OLD.ID_CODIGO_BARRA,'P',v_accion, 'ESTADO', :OLD.ESTADO, :NEW.ESTADO);
       END IF;
    END IF;
END;
