TriggerOraclev10g.body:
DECLARE
   wNumerador  NUMBER;
   v_accion VARCHAR2(1) := NULL;   
BEGIN

    IF NVL(:OLD.IS_DELETED,0) != NVL(:NEW.IS_DELETED,0) THEN
       IF :NEW.IS_DELETED = 'Y' AND :OLD.WF_STATUS != 'ELI' THEN
          v_accion := 'B';
       ELSE
          IF NVL(:NEW.WF_STATUS,0) != 'ELI' THEN
             v_accion := 'A';
          END IF;
       END IF;

       IF v_accion IS NOT NULL THEN
          SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

          INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
          (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
          VALUES(wNumerador, 'AGENTE', 'RP_AGENTE_SUCURSAL', :OLD.ID_AGENTE_SUCURSAL,'P',v_accion, 'IS_DELETED', :OLD.IS_DELETED, :NEW.IS_DELETED);
       END IF;
    END IF;


    IF NVL(:OLD.WF_STATUS,0) != NVL(:NEW.WF_STATUS,0) THEN
       IF :NEW.WF_STATUS = 'ELI' AND :OLD.IS_DELETED = 'N' THEN
          v_accion := 'B';
       END IF;

       IF :NEW.WF_STATUS != 'ELI' AND :OLD.WF_STATUS = 'ELI' AND :OLD.IS_DELETED = 'N' THEN
           v_accion := 'A';
       END IF;

       IF v_accion IS NOT NULL THEN     
            SELECT RAPIPAGO.RP_SQ_NOVEDAD_COBERTURA.NEXTVAL into wNUMERADOR FROM DUAL;  

            INSERT INTO RAPIPAGO.WS_COBERTURA_NOVEDADES
                (ID_NOVEDAD, ID_GRUPO, TABLA, ID_TABLA, ESTADO, ACCION, CAMPO, VALOR_ANT, VALOR_NVO)
            VALUES(wNumerador, 'AGENTE', 'RP_AGENTE_SUCURSAL', :OLD.ID_AGENTE_SUCURSAL,'P',v_accion, 'WF_STATUS', :OLD.WF_STATUS, :NEW.WF_STATUS);
       END IF;

    END IF;

END;
